sys_variable_value.value:
answer = ifScript();
	function ifScript(){
		var p1count = 0;
		var gr = new GlideRecord('incident');
		gr.addQuery('problem_id', '=', current.sys_id);
		gr.addQuery('priority','=',1);
		gr.query();
		p1count = gr.getRowCount();
		workflow.scratchpad.priorityCount = p1count;
		if(p1count > 0) {
			return 'yes';
		} else {
			return 'no';
		}
	}

